import React, { useEffect, useState } from 'react'
import { Await, Link } from 'react-router-dom';
import './AddTrip.css'

const AddTrip = () => {


  // const [trip, setTrip] = useState([{
  //   user_id:"",
  //   start:"coimbatore",
  //   destination:"kasol",
  //   start_date:"02/04/2024",
  //   end_date:"08/04/2024",
  //   mode_of_travel:"bike",
  //   notes:" lorem20 dhcbhwd iyguywvcd ",
  // }])

  const [allTripPlans, setAllTripPlans] = useState([]);

  const fetchTripPlans = async () => {
    await fetch('https://travel-planner-4mrm.onrender.com/allTripPlans')  
    .then((res)=>res.json())
    .then((data=>{setAllTripPlans(data)}));
  }

  useEffect(()=>{
    fetchTripPlans();
  },[])

  const deleteTripPlan = async(id) =>{
    await fetch('https://travel-planner-4mrm.onrender.com/deleteTripPlan',{
      method:'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
      },
      body:JSON.stringify({id:id})
    })
    await fetchTripPlans();
  }

  



  return (
    <div  className='addtrip'>
      <div className="displaytrip">
          <Link  style={{textDecoration:"none"}} to='/create' >  <h4> Add Trip+ </h4> </Link> 
        <table>
          <thread>
            <tr className='head'>
              <th>START</th>
              <th>DESTINATION</th>
              <th>START DATE</th>
              <th>END DATE</th>
              <th>MODE OF TRAVEL</th>
              <th>NOTES</th>
              <th>ACTION</th>
            </tr>
            </thread>
            <tbody>
              {allTripPlans.map((trip,i)=>{
                return <tr key={i} className='list'>
                  <td> {trip.start} </td>
                  <td> {trip.destination} </td>
                  <td> {trip.start_date} </td>
                  <td> {trip.end_date} </td>
                  <td> {trip.mode} </td>
                  <td> {trip.notes} </td>
                  <td className='btnn' > <p> <Link to={`/update/${trip._id}`} className='updatebtn' > update </Link> </p>
                  <p onClick={()=>{deleteTripPlan(trip.id)}} className='deletebtn' > delete </p>
                  </td>
                {/* <hr /> */}
                  </tr>
              })}
            </tbody>

        </table>
      </div>
       
      
    </div>
  )
}

export default AddTrip
